import "./fonts/MSignUp.css";
import axios from "axios";
import { useLocation } from "react-router-dom";
import { useNavigate } from "react-router";
import React, { useEffect, useState } from "react";
import Rodal from 'rodal';

// include styles
import 'rodal/lib/rodal.css';


function Signin() {
  const location = useLocation();
  const history = useNavigate();
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [errModal, setErrModal] = useState(false);



  function handleClick() {
    axios.post("http://localhost:4000/api/resident/", {
      email: email,
      password: password,
    }).then((res) => {
      console.log(res);
      if (res.data.type == 0){
      history("/resdash", {
        state: {
          ...res.data
        }
      })}
      else if (res.data.type == 1) {
        alert("34")
      } else {
        setErrModal(true);
      }
    }).catch((err) => {
      alert(err)
    })
  }

  return (
    <div class="background">
      <Rodal visible={errModal} animation= "zoom" duration = {200} onClose={setErrModal(false)}>
          <div>Content</div>
        </Rodal>
      <div class="colorspace">
        <p class="heading">S I G N &nbsp; I N</p>
        <div class="shopimg" />
      </div>
      <div class="whitespace">
        <p class="loginheading">Log In&nbsp;&nbsp;&nbsp;</p>
        <input
          class="input"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        ></input>
        <input
          class="input"
          placeholder="Password"
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        ></input>
        <button class="nstep" onClick={handleClick}>Next Step</button>
        <div class="btext">
          <p class="btexta">Don't have an account?</p>
          <button
            class="btextb"
            onClick={(e) => {
              window.location.href = "/signup";
            }}
          >
            Sign Up
          </button>
        </div>
      </div>
    </div>
  );
}
export default Signin;
